import axios from 'axios';
import React from 'react'
import { useEffect } from 'react';
import { useState } from 'react'
import { Link } from 'react-router-dom';


const ProductAdd = () => {

  //slug generate
  const makeSlug = (data) => {
    let arr = data.split(' ');
    return arr.join('-').toLowerCase();
  }

  //add cat form
  const[addForm, setAddForm] = useState(false);

  //cat edit form
  const[editForm, setEditForm] = useState(false)

  //input cat data
  const[cat, setCat] = useState({
    name : '',
    id : ''
  });

  //get all cats
  const[cats, setCats] = useState([])

  useEffect(() => {
    axios.get('http://localhost:5050/categories').then( res => {
      setCats(res.data)
    })
  },[cats])

  //handle add form 
  const handleAddForm = () => {
    setAddForm(true)
    setEditForm(false)
    setCat({
      name : '',
      id : ''
    })
  }
  //handleCatFormSubmit
  const handleCatFormSubmit = (e) => {
    e.preventDefault()

    let slug = makeSlug(cat.name)

    axios.post('http://localhost:5050/categories',{
      id : '',
      name : cat.name,
      slug: slug
    }).then( res => {
      setAddForm(false)
      setCat({
        name : '',
        id : ''
      })
    })  
  }

  //delete cat
  const handleCatDelete = (id) => {
    axios.delete('http://localhost:5050/categories/' + id)
  }
  //edit form
  const handleEditForm = (id) => {
    setEditForm(true)
    setAddForm(false)

    axios.get('http://localhost:5050/categories/' + id).then( res =>{
      setCat({
        name : res.data.name,
        id : res.data.id
      })
    })
  }

  //update edit data
  const handleCatUpdate = (e) => {
    e.preventDefault()
    let slug = makeSlug(cat.name)

    axios.patch('http://localhost:5050/categories/'+ cat.id, {
      name : cat.name,
      slug : slug

    }).then(res => {
      setCat({
        name : '',
        id : ''
      })
      setEditForm(false)
    })

  }

  return (
    <>
      <h1>Add New Products</h1>
      <hr />
      <Link to="/admin/products" className='btn btn-sm btn-primary' onClick={ handleAddForm }>All Products</Link>

    </>
  )
}

export default ProductAdd
