import axios from 'axios'
import React, { useState } from 'react'
import { useEffect } from 'react'
import { Button, Table } from 'react-bootstrap'
import { Link } from 'react-router-dom'

const Tag = () => {

  const[ tags, setTags ] = useState([])

  useEffect(() => {
    axios.get(`http://localhost:5050/tags`).then(res => {
      setTags(res.data)
    }).catch(err =>{
      console.log(err);
    })

  },[tags])

  const handleDelete = (id) => {
    axios.delete(`http://localhost:5050/tags/${id}`)
  }
  return (
    <>
      <h1>Tags</h1>
      <hr />
      <Link to="/admin/add-tag" className='btn btn-sm btn-primary'>Add New Tag</Link>
      <hr />
      <Table>
        <thead>
          <tr>
            <th>#</th>
            <th>Name</th>
            <th>Slug</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {
            tags.map((data, index) => 
            <tr>
              <td>{index + 1}</td>
              <td>{data.name}</td>
              <td>{data.slug}</td>
              <td>
                <Button className="btn btn-sm" variant='warning'>Edit</Button>
                <Button className="btn btn-sm" variant='danger' onClick={() => handleDelete(data.id)}>Delete</Button>
              </td>
            </tr>
            )
          }
        </tbody>

      </Table>
    </>
  )
}

export default Tag
