import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { Button, Form, Table } from 'react-bootstrap'
import { Link } from 'react-router-dom'

const Tags = () => {

  //edit tag state
  const[tag, setTag] = useState('')

  //tag state
  const [ tags, setTags] = useState([]);

  //to deltele tags
  const handleTagDelete = (id) => {
    axios.delete('http://localhost:5050/tags/' + id )
  }


  useEffect(() => {
    axios.get('http://localhost:5050/tags').then(res => {
      setTags(res.data.reverse())
    })

  },[tags])

  return (
    <>
      <h1>Tags</h1>
      <hr />
      <Link to="/admin/add-tag" className='btn-sm btn btn-primary'>Add new Tags</Link>
      <hr />

      <Table> 
        <thead>
          <tr>
            <th>#</th>
            <th>Name</th>
            <th>slug</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {
              tags.map((data, index) => 
              <tr>
              <td>{index + 1}</td>
              <td>{data.name}</td>
              <td>{data.slug}</td>
              <td>
                <Button variant="warning" className='btn-sm'>Edit</Button>
                <Button variant="danger" className='btn-sm' onClick={ () => handleTagDelete(data.id)}>Delete</Button>
              </td>
            </tr>
              )
            }

        </tbody>
      </Table>

      <hr />
      <Form >
            <Form.Group my={ 3 }>
                <Form.Control type='text' placeholder='Tag Name' value={ tag } onChange = { e => setTag(e.target.value)}/>
            </Form.Group>
            <br />
            <Form.Group my={ 3 }>
                <Button type='submit' variant='success' className='btn-sm'>Update</Button>
            </Form.Group>
        </Form>
    </>
  )
}

export default Tags
