import axios from 'axios';
import React from 'react'
import { useState } from 'react'
import { Button, Form, Table } from 'react-bootstrap'


const Category = () => {
  //slug generate
  const makeSlug = (data) => {
    let arr = data.split(' ');
    return arr.join('-').toLowerCase();
  }

  //add cat form
  const [addForm, setAddForm] = useState(false);

  //input cat data
  const[cat, setCat] = useState({
    name : '',
    id : ''
  });

  //handle add form
  const handleAddForm = () => {
    setAddForm(true)
  }
  //handleCatFormSubmit
  const handleCatFormSubmit = (e) => {
    e.preventDefault()

    let slug = makeSlug(cat.name)

    axios.post('http://localhost:5050/categories',{
      id : '',
      name : cat.name,
      slug: slug
    }).then( res => {
      setAddForm(false)
    })

  }
  return (
    <>
      <h1>Category</h1>
      <hr />
      <Button className='btn btn-sm' onClick={ handleAddForm }>Add New Category</Button>
      <hr />

      {
        addForm &&
        <>
          <Form onClick={ handleCatFormSubmit }>
          <Form.Group my ={3}>
              <Form.Control type='text'  placeholder='Tag Name' value={cat} onChange={ e => setCat({ ...cat, name: e.target.value})}/>
          </Form.Group>
          <br />
          <Form.Group my ={3}>
              <Button type='submit' variant='success' >Update</Button>
          </Form.Group>
          </Form>
        </>
      }

      <Table>
        <thead>
          <tr>
            <th>#</th>
            <th>Name</th>
            <th>Slug</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>1</td>
            <td>MEN</td>
            <td>men</td>
            <td>
              <Button className="btn btn-sm" variant='info'>View</Button>
              <Button className="btn btn-sm" variant='warning'>Edit</Button>
              <Button className="btn btn-sm" variant='danger'>Delete</Button>
            </td>
          </tr>
        </tbody>

      </Table>
    </>
  )
}

export default Category
