import React, { useState } from 'react'
import { Button, Form, Table } from 'react-bootstrap'


const Category = () => {

  //add cat form
  const [addForm, setAddForm] = useState(false);

  //input cat data
  const [cat, setCat] = useState({
    name : '',
    id : ''

  });

  //handle add form
  const handleAddForm = () =>{
    setAddForm(true);
  }
  //handle form sumbit
  const handleCatFormSubmit = (e) => {
    e.preventDefault();
    setAddForm(false);

  }


  return (
    <>
      <h1>Category</h1>
      <hr />

      <Button className='btn-sm' onClick={handleAddForm}>Add new Category </Button>
      <hr />

      <>

      {
        addForm &&
        <Form onSubmit={ handleCatFormSubmit }>
              <Form.Group my={ 3 }>
                  <Form.Control type='text' placeholder='Cat Name'value={cat.name} onChange={ e => setCat({...cat, name:e.target.value})}/>
              </Form.Group>

              <br />
              <Form.Group my={ 3 }>
                  <Button type='submit' variant='success' className='btn-sm' >Add</Button>
              </Form.Group>
          </Form>
      }

       </>


      <Table > 
        <thead>
          <tr>
            <th>#</th>
            <th>Name</th>
            <th>slug</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>1</td>
            <td>Men</td>
            <td>men</td>
            <td>
              <Button variant="success" className='btn-sm'>View</Button>
              <Button variant="warning" className='btn-sm'>Edit</Button>
              <Button variant="danger" className='btn-sm'>Delete</Button>
            </td>
          </tr>
        </tbody>
      </Table>
    </>
  )
}

export default Category
