import axios from 'axios';
import React, { useState } from 'react'
import { Button, Form } from 'react-bootstrap'
import { Link } from 'react-router-dom'

const AddTag = () => {

  //input state
  const [ tag, setTag ] = useState();

  //make slug

  const makeSlug = (data) => {
    let arr = data.split(' ');
    return arr.join('-').toLowercase();

  }
  console.log(makeSlug('kire kemon asis'));

  //add Tag
  const handleTagAdd = (e) => {
    e.preventDefault()
    let slug = makeSlug(tag)

    axios.post('http://localhost:5050/tags',{
      id : '',
      name : tag,
      slug : slug

    }).then()

  }

  return (
    <>
        <h1>Add Nue Tag</h1>
        <hr />
        <Link to="/admin/tag" className='btn btn-primary btn-sm'>All Tags</Link>

        <hr />
        <Form onSubmit={ handleTagAdd}>
            <Form.Group my={ 3 }>
                <Form.Control type='text' placeholder='Tag Name' value={ tag } onChange = { e => setTag(e.preventDefault.value)}/>
            </Form.Group>
            <br />
            <Form.Group my={ 3 }>
                <Button type='submit' variant='success' className='btn-sm'>Add</Button>
            </Form.Group>
        </Form>
    </>
  )
}

export default AddTag
