import axios from 'axios';
import React from 'react'
import { useEffect } from 'react';
import { useState } from 'react'
import { Button, Form, Table } from 'react-bootstrap'


const Category = () => {

  //slug generate
  const makeSlug = (data) => {
    let arr = data.split(' ');
    return arr.join('-').toLowerCase();
  }

  //add cat form
  const[addForm, setAddForm] = useState(false);

  //cat edit form
  const[editForm, setEditForm] = useState(true)

  //input cat data
  const[cat, setCat] = useState({
    name : '',
    id : ''
  });

  //get all cats
  const[cats, setCats] = useState([])

  useEffect(() => {
    axios.get('http://localhost:5050/categories').then( res => {
      setCats(res.data)
    })
  },[cats])

  //handle add form 
  const handleAddForm = () => {
    setAddForm(true)
  }
  //handleCatFormSubmit
  const handleCatFormSubmit = (e) => {
    e.preventDefault()

    let slug = makeSlug(cat.name)

    axios.post('http://localhost:5050/categories',{
      id : '',
      name : cat.name,
      slug: slug
    }).then( res => {
      setAddForm(false)
      setCat({
        name : '',
        id : ''
      })
    })

  }

  //delete cat
  const handleCatDelete = (id) => {
    axios.delete('http://localhost:5050/categories/' + id)
  }


  return (
    <>
      <h1>Category</h1>
      <hr />
      <Button className='btn btn-sm' onClick={ handleAddForm }>Add New Category</Button>
      <hr />

      <Table>
        <thead>
          <tr>
            <th>#</th>
            <th>Name</th>
            <th>Slug</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {
            cats.map((data, index) => 
            <tr>
              <td>{index + 1}</td>
              <td>{ data.name }</td>
              <td>{ data.slug }</td>
              <td>
                <Button className="btn btn-sm" variant='warning'>Edit</Button>
                <Button className="btn btn-sm" variant='danger' onClick={() => handleCatDelete(data.id)}>Delete</Button>
              </td>
            </tr>
            )
          }
        </tbody>

      </Table>
      {
        addForm &&
        <>
          <Form onSubmit={ handleCatFormSubmit }>
          <Form.Group my ={3}>
              <Form.Control type='text'  placeholder='Cat Name' value={cat.name} onChange={ e => setCat({ ...cat, name: e.target.value})}/>
          </Form.Group>
          <br />
          <Form.Group my ={3}>
              <Button type='submit' variant='success' >Update</Button>
          </Form.Group>
          </Form>
        </>
      }

    {
        editForm &&
        <>
          <Form onSubmit={ handleCatFormSubmit }>
          <Form.Group my ={3}>
              <Form.Control type='text'  placeholder='Cat Name' value={cat.name} onChange={ e => setCat({ ...cat, name: e.target.value})}/>
          </Form.Group>
          <br />
          <Form.Group my ={3}>
              <Button type='submit' variant='success' >Update</Button>
          </Form.Group>
          </Form>
        </>
      }
    </>
  )
}

export default Category
