import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { Button, Form, Table } from 'react-bootstrap'
import { Link } from 'react-router-dom';


const ProductAdd = () => {

   //making slug
   const makeSlug = (data) => {
    let arr = data.split(' ');
    return arr.join('-').toLowerCase();

  }


  //add cat form
  const [addForm, setAddForm] = useState(false);
  const [editForm, setEditForm] = useState(false);

  //input cat data
  const [cat, setCat] = useState({
    name : '',
    id : ''

  });

  //showing all devs in DOM
  const [cats, setCats] = useState([]);

  //handle add form
  const handleAddForm = () =>{
    setAddForm(true);
    setEditForm(false)
    setCat({
      name : '',
      id : ''
    })
  }
  //handle form sumbit
  const handleCatFormSubmit = (e) => {
    e.preventDefault();

    let slug = makeSlug(cat.name)

    axios.post('http://localhost:5050/category', {
      id: '',
      name : cat.name,
      slug : slug
    }).then( res => {
      setAddForm(false);
      setCat({
        name : '',
        id : ''
      })
    })

  }
  //delete category
  const handleCatDelete = (id) => {
    axios.delete('http://localhost:5050/category/' + id)

  }
  //edit cat data
  const handleEdit = (id) => {
    setAddForm(false);
    setEditForm(true)
    axios.get('http://localhost:5050/category/' + id).then( res =>{
      setCat({
        name : res.data.name,
        id : res.data.id
      })
    })

  }
  // update cat data
  const handleCatUpdate = (e) => {
    e.preventDefault();
    let slug = makeSlug(cat.name)
    axios.patch('http://localhost:5050/category/' + cat.id, {
      name : cat.name,
      slug : slug
    }).then( res => {
      setCat({
        name : '',
        id : ''
      })
      setEditForm(false);
    })

  }

  //fetching data to show in DOM
  useEffect(() => {
    axios.get('http://localhost:5050/category').then( res => {
      setCats(res.data)
    })
  },[cats])


  return (
    <>
      <h1>Add new products</h1>
      <hr />

      <Link to="/admin/products" className='btn-sm btn btn-primary' onClick={handleAddForm}>All Products</Link>
      <hr />






        {
          editForm &&

          <>
          <h2>Edit Category</h2>
            <Form onSubmit={ handleCatUpdate }>
                <Form.Group my={ 3 }>
                    <Form.Control type='text' placeholder='Cat Name' value={cat.name} onChange={ e => setCat({...cat, name:e.target.value})}/>
                </Form.Group>

                <br />
                <Form.Group my={ 3 }>
                    <Button type='submit' variant='success' className='btn-sm' >Add</Button>
                </Form.Group>
            </Form>
          </>
        }

       </>

  )
}

export default ProductAdd
