import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { Button, Form, Table } from 'react-bootstrap'


const Category = () => {

   //making slug
   const makeSlug = (data) => {
    let arr = data.split(' ');
    return arr.join('-').toLowerCase();

  }

  //add cat form
  const [addForm, setAddForm] = useState(false);

  //input cat data
  const [cat, setCat] = useState({
    name : '',
    id : ''

  });

  //showing all devs in DOM
  const [cats, setCats] = useState([]);

  //handle add form
  const handleAddForm = () =>{
    setAddForm(true);
  }
  //handle form sumbit
  const handleCatFormSubmit = (e) => {
    e.preventDefault();

    let slug = makeSlug(cat.name)

    axios.post('http://localhost:5050/category', {
      id: '',
      name : cat.name,
      slug : slug
    }).then( res => {
      setAddForm(false);
      setCats({
        name : '',
        id : '',
        slug : ''
      })
    })

  }

  //fetching data to show in DOM
  useEffect(() => {
    axios.get('http://localhost:5050/category').then( res => {
      setCats(res.data)
    })
  },[cats])


  return (
    <>
      <h1>Category</h1>
      <hr />

      <Button className='btn-sm' onClick={handleAddForm}>Add new Category </Button>
      <hr />

      <>

      {
        addForm &&
        <Form onSubmit={ handleCatFormSubmit }>
              <Form.Group my={ 3 }>
                  <Form.Control type='text' placeholder='Cat Name' value={cat.name} onChange={ e => setCat({...cat, name:e.target.value})}/>
              </Form.Group>

              <br />
              <Form.Group my={ 3 }>
                  <Button type='submit' variant='success' className='btn-sm' >Add</Button>
              </Form.Group>
          </Form>
      }

       </>


      <Table > 
        <thead>
          <tr>
            <th>#</th>
            <th>Name</th>
            <th>slug</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {
            cats.map((data, index) =>
            <tr>
              <td>{index + 1}</td>
              <td>{data.name}</td>
              <td>{data.slug}</td>
              <td>
                <Button variant="success" className='btn-sm'>View</Button>
                <Button variant="warning" className='btn-sm'>Edit</Button>
                <Button variant="danger" className='btn-sm'>Delete</Button>
              </td>
            </tr>
            )
          }


        </tbody>
      </Table>
    </>
  )
}

export default Category
