import axios from 'axios';
import { useEffect, useState } from 'react';
import { Route, Routes } from 'react-router-dom';
import './App.css';
import AddTag from './components/Admin/AddTag';
import Category from './components/Admin/Category';
import Dash from './components/Admin/Dash';
import Dashboards from './components/Admin/Dashboards';
import ProductAdd from './components/Admin/ProductAdd';
import Products from './components/Admin/Products';
import Tag from './components/Admin/Tag';
import Footers from './components/Footer/Footers';
import Headers from './components/Header/Headers';
import HomePage from './components/Pages/HomePage';
import ProductSingles from './components/Pages/ProductSingles';
import Shop from './components/Pages/Shop';

import './_assets/css/bundle.css'
import './_assets/css/style.css'


function App() {

  //tag state
  const[ tags, setTags ] = useState([])

  useEffect(() => {
    axios.get(`http://localhost:5050/tags`).then(res => {
      setTags(res.data)
    }).catch(err =>{
      console.log(err);
    })

  },[tags])

  return (
    <>

      <Headers></Headers>
        <Routes>
          <Route path='/' element={<HomePage></HomePage>}></Route>
          <Route path='/shop' element={<Shop></Shop>}></Route>
          <Route path='/shop/:id' element={<ProductSingles></ProductSingles>}></Route>
          <Route path='/admin' element={<Dashboards></Dashboards>}>
            <Route path='/admin/products' element={<Products></Products>}></Route>
            <Route path='/admin/add-products' element={<ProductAdd></ProductAdd>}></Route>
            <Route path='/admin/dash' element={<Dash></Dash>}></Route>
            <Route path='/admin/category' element={<Category></Category>}></Route>
            <Route path='/admin/tag' element={<Tag tags={tags}></Tag>}></Route>
            <Route path='/admin/add-tag' element={<AddTag></AddTag>}></Route>
          </Route>
        </Routes>
        <Footers></Footers>
    </>
  );
}

export default App;
